import groovy.json.*


def _globalParent = context.expand('${#Project#globalParent}')
def response = context.expand( '${List Discussions#Response}' ).toString()
def json = new JsonSlurper().parseText(response) 

assert json.findAll{it.general == true}.size() == 1;

json.findAll{it.general == true}.each{ value ->
	assert value.guid != null
	assert value.description.isEmpty()
	assert value.name ==  "workportal-project-discussion-generaldiscussion"
	assert value.date != null
	assert value.globalParent == _globalParent
	assert value.attachments.isEmpty()
	assert value.tags.isEmpty()
}

