import groovy.json.*
import java.util.UUID

def _globalParent = context.expand('${#Project#globalParent}')

int numberOfDiscussions = testRunner.testCase.testSuite.getPropertyValue("numberOfDiscussions") as int
def _date = new Date().getTime();
for (int i = 1; i <= numberOfDiscussions; i++) {

	def _guid = UUID.randomUUID().toString()
	def json = new JsonBuilder()
	_date = _date + 1
	json {
		content {
			guid(_guid)
			description("Descripcion de $_guid")
			name("Nombre de $_guid")
			date(_date)
			user(1)
			general(false)
			globalParent(_globalParent)
			tags([]) 
			attachments([]) 			
		}
		attachmentsToCreate([]) 
		attachmentsToDelete([]) 
	}
	testRunner.testCase.testSuite.setPropertyValue("discussion$i", json.toString())

}

