import groovy.json.*

def response = context.expand( '${List Discussions#Response}' ).toString()
def json = new JsonSlurper().parseText(response) 

int numberOfDiscussions = context.expand('${#TestSuite#numberOfDiscussions}') as int
//def list = []
List<String> list = new ArrayList<String>()
for (int i = 1; i <= numberOfDiscussions; i++) {
	def propiedad = '${#TestSuite#discussion'+ "${i}}"
	list.add(new JsonSlurper().parseText(context.expand(propiedad)))
}

list.reverse(true)
