import groovy.json.*
import java.util.UUID

def _globalParent = context.expand('${#Project#globalParent}')

int numberOfFiles = testRunner.testCase.testSuite.getPropertyValue("numberOfFiles") as int
def _date = new Date().getTime() - 30;
for (int i = 1; i <= numberOfFiles; i++) {
	def _guid = UUID.randomUUID().toString()
	def guidAttachment =  UUID.randomUUID().toString()
	def json = new JsonBuilder()
	_date = _date + 1
	json {
		content {
			guid(_guid)
			description("Descripcion de $_guid")
			name("Nombre de $_guid")
			date(_date)
			user(1)
			globalParent(_globalParent)
			tags([]) 
			attachment {
				guid(guidAttachment)
				name("Archivo.txt")
			}
		}
		attachmentsToCreate([]) 
		attachmentsToDelete([]) 		

	}
	testRunner.testCase.testSuite.setPropertyValue("file$i", json.toString())
	testRunner.testCase.testSuite.setPropertyValue("file", json.toString())
	testRunner.runTestStepByName( "Create File")
}

testRunner.gotoStepByName( "Error - Create File Same GUID")

