testRunner.testCase.testSuite.removeProperty("comment")

int numberOfComments = testRunner.testCase.testSuite.getPropertyValue("numberOfComments") as int

for (int i = 1; i <= numberOfComments; i++) {
	testRunner.testCase.testSuite.removeProperty("comment${i}")
}

import groovy.json.*
import java.util.UUID

def _globalParent = context.expand('${#Project#globalParent}')

int numberOfComments = testRunner.testCase.testSuite.getPropertyValue("numberOfComments") as int
int numberOfDiscussions = testRunner.testCase.testSuite.getPropertyValue("numberOfDiscussions") as int

random = new Random()

def _date = new Date().getTime();
for (int i = 1; i <= numberOfComments; i++) {
	randomInt = random.nextInt(numberOfDiscussions)+1

	def propDiscusssion = '${#TestSuite#discussion'+ "${randomInt}}"
	def _parent = new JsonSlurper().parseText(context.expand(propDiscusssion)).content.guid 
	def _guid = UUID.randomUUID().toString()
	def json = new JsonBuilder()
	_date = _date + 1
	json {
		content {
			guid(_guid)
			text("Texto de $_guid")
			date(_date)
			user(1)
			parent(_parent)
			attachments([]) 
		}
		attachmentsToCreate([]) 
		attachmentsToDelete([]) 			
	}
	testRunner.testCase.testSuite.setPropertyValue("comment${i}", json.toString())
	testRunner.testCase.testSuite.setPropertyValue("comment", json.toString())
	testRunner.runTestStepByName( "Create Comment")
}

testRunner.gotoStepByName( "Error - Create Comments Same GUID")

