import groovy.json.*
import java.util.UUID

//Carga todas las discusiones
int numberOfDiscussions = context.expand('${#TestSuite#numberOfDiscussions}') as int
List<String> guid_discusiones = new ArrayList<String>()
for (int i = 1; i <= numberOfDiscussions; i++) {
	def propiedad = '${#TestSuite#discussion'+ "${i}}"
	guid_discusiones.add(new JsonSlurper().parseText(context.expand(propiedad)).content.guid)
}

//Carga todos los comentarios
int numberOfComments = context.expand('${#TestSuite#numberOfComments}') as int
List<JsonSlurper> comentarios = new ArrayList<JsonSlurper>()
for (int i = 1; i <= numberOfComments; i++) {
	def propiedad = '${#TestSuite#comment'+ "${i}}"
	comentarios.add(new JsonSlurper().parseText(context.expand(propiedad)))
}

guid_discusiones.each{ value ->
	testRunner.testCase.setPropertyValue("idParent", value)
	def count = comentarios.findAll{it.content.parent == value}.size() as int
	testRunner.testCase.setPropertyValue("count", String.valueOf(count));

	//Ejecuta el listado de comentario con los anteriores parametros
	testRunner.runTestStepByName( "List Comments")
}

