import groovy.json.*

def response = context.expand( '${List Comments#Response}' ).toString()
def jsonResponse = new JsonSlurper().parseText(response) 
def idParent = context.expand( '${#TestCase#idParent}')
def count = context.expand( '${#TestCase#count}')

//Carga todos los comentarios
int numberOfComments = context.expand('${#TestSuite#numberOfComments}') as int
List<JsonSlurper> comentarios = new ArrayList<JsonSlurper>()
for (int i = 1; i <= numberOfComments; i++) {
	def propiedad = '${#TestSuite#comment'+ "${i}}"
	comentarios.add(new JsonSlurper().parseText(context.expand(propiedad)))
}
comentarios.reverse(true)

List<JsonSlurper> comentariosFiltrado = comentarios.findAll{it.content.parent == idParent};


assert comentariosFiltrado.size == jsonResponse.size

jsonResponse.eachWithIndex{ value, index ->
	assert value == comentariosFiltrado[index].content
}

