assert 2 == jsonResponse.findAll(query1).size assert 2 == jsonResponse.findAll(query2).size
jsonResponse.each{ value -> assert value.description == null assert value.allowEdition == true assert value.idPlan == context.expand('${#TestCase#idPlan}') assert value.userAssigned == 1 assert value.progress == 0 assert value.duration == null assert value.startDate == null assert value.estimatedFinishDate == null assert value.finishDate == null }

