con:configuration.scriptText:
import groovy.json.*

def response = context.expand( '${Validate Activity Info#Response}' ).toString()
def jsonResponse = new JsonSlurper().parseText(response)
def secondsDiff = Math.abs(1432049917548-jsonResponse.startDate);

assert context.expand('${#TestCase#idActivity1}') == jsonResponse.id
assert "Activity Edited" == jsonResponse.name
assert "My Description" == jsonResponse.description
assert jsonResponse.allowEdition
assert context.expand('${#TestCase#idPlan}') == jsonResponse.idPlan
assert 1 == jsonResponse.userAssigned
assert 0 == jsonResponse.progress
assert 2 == jsonResponse.duration
assert secondsDiff <= 1
assert null == jsonResponse.estimatedFinishDate
assert null == jsonResponse.finishDate

def items = new JsonSlurper().parseText(
'''
[
    {
        "name": "Item 1",
        "resolved": false
    },
    {
        "name": "Item 2",
        "resolved": false
    }
]
''' )

assert items == jsonResponse.items

